```
async function createDocument(
  collection: string,
  data: any,
): Promise<TypeWithID> {
  return payload
    .create({
      collection: collection,
      disableVerificationEmail: true,
      data: {
        ...data,
        parent: null,
      },
    })
    .then((doc) => {
      if (data.parent) {
        return setDocumentParent(collection, data.id, data.parent);
      }
      return doc;
    });
}

async function updateDocument(
  collection: string,
  id: string,
  data: any,
): Promise<TypeWithID> {
  return payload
    .update({
      collection: collection,
      data: data,
      id: id,
    })
    .then((doc) => {
      if (data.parent) {
        return setDocumentParent(collection, data.id, data.parent);
      }
      return doc;
    });
}

